total_laranjas = 4 # declaração da variável que armazena o número total de laranjas
laranjas_comidas = 3 # declaração da variável que armazena o número de laranjas comidas
laranjas_restantes = total_laranjas - laranjas_comidas # subtração do número de laranjas comidas do total de laranjas para obter o número de laranjas restantes
print("Sobraram", laranjas_restantes, "laranjas.") # exibição do resultado